import React, { useContext, useRef, useState } from "react";
import { AuthContext } from "../../context/AuthContext";
import { useHistory } from "react-router";
import data from "../../assets/data.js";
import { Context } from "../../context/langContext";
import "./AuthForm.css";

export default function SignIn() {
  const { lang } = useContext(Context);
  const [error, setError] = useState("");
  const history = useHistory();
  const { login } = useContext(AuthContext);

  const inputs = useRef([]);

  const addInputs = (el) => {
    if (el && !inputs.current.includes(el)) {
      inputs.current.push(el);
    }
  };

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      await login(inputs.current[0].value, inputs.current[1].value);
      history.push("/calculator");
    } catch {
      setError(data[lang].error);
    }
  }

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit} autoComplete="off">
        <h2>{data[lang].connect}</h2>
        <div className="form-group">
          <label class="form-label" htmlFor="mail">
            Votre mail
          </label>
          <input
            type="email"
            ref={addInputs}
            htmlFor="email"
            required
            autoComplete="off"
            placeholder="Entrez votre mail"
          />
        </div>
        <div className="form-group">
          <label htmlFor="psw">Votre mot de passe</label>
          <input
            type="password"
            ref={addInputs}
            htmlFor="psw"
            id="psw"
            required
            autoComplete="off"
            placeholder="Entrez votre mot de passe"
          />
        </div>
        <div className="error">{error}</div>
        <button className="btn-sign"> Se connecter</button>
      </form>
    </div>
  );
}
