import React, { useState, useEffect } from "react";
import "./Calculator.css";

export default function Calculator() {
  const [preState, setPreState] = useState("");
  const [curState, setCurState] = useState("");
  const [input, setInput] = useState("0");
  const [operator, setOperator] = useState(null);
  const [total, setTotal] = useState(false);

  const inputNum = (e) => {
    if (curState.includes(".") && e.target.innerText === ".") return;
    if (total) {
      setPreState("");
    }

    curState
      ? setCurState((pre) => pre + e.target.innerText)
      : setCurState(e.target.innerText);
    setTotal(false);
  };

  useEffect(() => {
    setInput(curState);
  }, [curState]);

  useEffect(() => {
    setInput("0");
  }, []);

  const operatorType = (e) => {
    setTotal(false);
    setOperator(e.target.innerText);
    if (curState === "") return;
    if (preState !== "") {
      equals();
    } else {
      setPreState(curState);
      setCurState("");
    }
  };

  const equals = (e) => {
    if (e?.target.innerText === "=") { setTotal(true)}

    let calc;
    switch (operator) {
      case "/":
        calc = String(parseFloat(preState) / parseFloat(curState));
        break;
      case "+":
        calc = String(parseFloat(preState) + parseFloat(curState));
        break;
      case "-":
        calc = String(parseFloat(preState) - parseFloat(curState));
        break;
      case "X":
        calc = String(parseFloat(preState) * parseFloat(curState));
        break;

      default:
        return;
    }
    setInput("");
    setPreState(calc);
    setCurState("");
  };

  const minusPlus = () => {};
  const percent = () => {};

  const reset = () => {
    setPreState("");
    setCurState("");
    setInput("0");
  };

  return (
    <div className="container">
      <div className="wrapper">
        <div className="screen">{input}</div>
        <div onClick={reset} className="btn light-gray">
          AC
        </div>
        <div onClick={percent} className="btn light-gray">
          %
        </div>
        <div onClick={minusPlus} className="btn light-gray">
          +/-
        </div>
        <div onClick={operatorType} className="btn orange">
          /
        </div>
        <div onClick={inputNum} className="btn">
          7
        </div>
        <div onClick={inputNum} className="btn">
          8
        </div>
        <div onClick={inputNum} className="btn">
          9
        </div>
        <div onClick={operatorType} className="btn orange">
          X
        </div>
        <div onClick={inputNum} className="btn">
          4
        </div>
        <div onClick={inputNum} className="btn">
          5
        </div>
        <div onClick={inputNum} className="btn">
          6
        </div>
        <div onClick={operatorType} className="btn orange">
          +
        </div>
        <div onClick={inputNum} className="btn">
          1
        </div>
        <div onClick={inputNum} className="btn">
          2
        </div>
        <div onClick={inputNum} className="btn">
          3
        </div>
        <div onClick={operatorType} className="btn orange">
          -
        </div>
        <div onClick={inputNum} className="btn zero">
          0
        </div>
        <div onClick={inputNum} className="btn">
          .
        </div>
        <div onClick={equals} className="btn">
          =
        </div>
      </div>
    </div>
  );
}
