const initialState = {
  showAuth: true,
};

export default function modalsReducer(state = initialState, action) {
  switch (action.type) {
    case "TOGGLEAUTH":
      if (state.showAuth) {
        console.log(state.showAuth)
        return {
          ...state,
          showAuth: false,
        };
      } else {
        return {

          ...state,
          showAuth: true,
        };
      }
    default:
      return;
  }
}
