import React, { useContext, useRef, useState } from "react";
import { AuthContext } from "../../context/AuthContext";
import { useHistory } from "react-router";

export default function SignUp() {
  const [error, setError] = useState("");
  const history = useHistory();
  const { signUp } = useContext(AuthContext);

  const inputs = useRef([]);

  const addInputs = (el) => {
    if (el && !inputs.current.includes(el)) {
      inputs.current.push(el);
    }
  };

  return (
    <div className="">
      <form className="form-auth">
        <h2>inscription</h2>
        <label htmlFor="mail">Votre mail</label>
        <input type="email" onChange={addInputs} htmlFor="email" required />
        <label htmlFor="psw">Votre mot de passe</label>
        <input type="password" onChange={addInputs} id="psw" required />
        <label htmlFor="confirmpsw">Confirmez le mot de passe</label>
        <input type="password" onChange={addInputs} id="confirmpsw" required />
        <button className="btn-sign">S'inscrire</button>
      </form>
      <button className="btn-close">X</button>
      <p>Besoin de cr√©er un compte ? </p>
    </div>
  );
}
