import React, { useContext, useRef, useState } from "react";
import { AuthContext } from "../../context/AuthContext";
import { useHistory } from "react-router";
import "./AuthForm.css";

export default function SignIn() {
  const [error, setError] = useState("");
  const history = useHistory();
  const { login } = useContext(AuthContext);

  const inputs = useRef([]);

  const addInputs = (el) => {
    if (el && !inputs.current.includes(el)) {
      inputs.current.push(el);
    }
  };

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      await login(inputs.current[0].value, inputs.current[1].value);
      history.push("/calculator");
    } catch {
      setError("Email ou mot de passe incorrect.");
    }
  }

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit} className="form-auth">
        <h2>connexion</h2>

        <input class="form-input" type="email" ref={addInputs} htmlFor="email" required />
        <label class="form-label" htmlFor="mail">Votre mail</label>
        <label htmlFor="psw">Votre mot de passe</label>
        <input
          type="password"
          ref={addInputs}
          htmlFor="psw"
          id="psw"
          required
        />
        {error}
        <button className="btn-sign"> Se connecter</button>
      </form>
    </div>
  );
}
