import React, { useContext, useRef, useState, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import { useHistory } from "react-router";
import "./AuthForm.css";

export default function SignUp() {
  const [error, setError] = useState("");
  const history = useHistory();
  const { signUp } = useContext(AuthContext);

  const inputs = useRef([]);

  const addInputs = (el) => {
    if (el && !inputs.current.includes(el)) {
      inputs.current.push(el);
    }
  };

  async function handleSubmit(e) {
    e.preventDefault();

    if (inputs.current[1].value !== inputs.current[2].value) {
      setError("Les mots de passes ne sont pas identiques");
    }

    await signUp(inputs.current[0].value, inputs.current[1].value);
    history.push("/calculator");
  }

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit} className="form-auth">
        <h2>inscription</h2>
        <label htmlFor="mail">Votre mail</label>
        <input
          type="email"
          ref={addInputs}
          htmlFor="email"
          required
          autoComplete="off"
        />
        <label htmlFor="psw">Votre mot de passe</label>
        <input
          type="password"
          ref={addInputs}
          id="psw"
          required
          autoComplete="off"
        />
        <label htmlFor="confirmpsw">Confirmez le mot de passe</label>
        <input type="password" ref={addInputs} id="confirmpsw" required />
        {error}
        <button className="btn-sign">S'inscrire</button>
      </form>
    </div>
  );
}
